import React from "react";
import logo from '../newtradr-logo.svg';

function AboutUs() {
    return (
        <>
        <div className='main-container'>
          <h2>Welcome to NewTradr.</h2>
          <p className='advice'>Our goal is to introduce investing to people who may not consider themselves
            the "investor type." We provide an easy, friendly introduction to a few companies for initial stock buys.
            <br />
            <br />
            Our survey is based on demonstrated research linking certain areas of behavior to risk appetite (such as driving and socialization).
            However, we know that it is more fun to buy stock in a company that does something you care about,
            so we make sure to ask for your interests as well!
            </p>
        </div>
        </>
      );
}

export default AboutUs;
